<?phpsession_start();spl_autoload_register(function($class){ include 'c/' . $class . '.class.php';});if($_GET['c'] == 'user'){ $controller = new User(); if(!isset($_GET['action'])){  $_GET['action'] = 'authorisation'; }} else if($_GET['c'] == 'page'){ $controller = new Page(); if(!isset($_GET['action'])){  $_GET['action'] = 'read'; }} else{ $controller = new Market(); if(!isset($_GET['action'])){  $_GET['action'] = 'getOffers'; }}$controller->Request("action_{$_GET['action']}");